import statisticsdata="100,30,53,67,85,87,50,45,51,72,64,69,59,17,22,23,44,25,16,67,85,87,50,45,51,72,59,14,50,55,32,23,24,25,37,28,39,30,33,35,40,34,41,43,94,95,59,98,99,44,45,47,48,49,53,61,63,69,75,77,60,83" grades = data.split(',')grades = list(map(int, grades))minGradeValue = min(grades)print("the minimum grade value is", minGradeValue)maxGradeValue = max(grades)print("the maximum grade value is", maxGradeValue)sumOfGrades = sum(grades)noOfItems = len(grades)averageOfGrades = sumOfGrades / noOfItemsprint(round(averageOfGrades,2))meanOfGrades = statistics.mean(grades)print(meanOfGrades)medianOfGrades = statistics.median(grades)print (medianOfGrades)outputStr = "The minimum grade value is {}. The maximum grade value is {}. The average grade value is {}. The mean grade value is {}. The median grade value is {}"print(outputStr.format(minGradeValue,maxGradeValue,averageOfGrades,meanOfGrades,medianOfGrades))